mapper.select:
select distinct plan.* from plan join dailyplan on plan.PLAN_NO = dailyplan.PLAN_NO where state=2

	and city_no=#{city_no}


	and season=#{season}


	and thema=#{thema}


	
		
			and days between #{days}-2 and #{days}
		
		
			and days between #{days}-2 and #{days}
		
		
			and days between #{days}-2 and #{days}
		
		
			and days > #{days}
		
	

order by s_date desc
